$(document).ready(function(){
$(".footnoted").each(function(e){var t=e+1;
$(this).html("<a>"+t+"</a>")});
$(".footnoted").each(function(e){this.id="reading-position-"+(e+1)});
$(".footnoted a").each(function(e){this.href="#footnote-"+(e+1)});
$("#footnotes p").each(function(e){this.id="footnote-"+(e+1)});
$("#footnotes p").append('<span class="return-to-reading footnoted" style="display:none;"><a> &uarr; Back to reading position</a></span>');
$("#footnotes span a").each(function(e){this.href="#reading-position-"+(e+1)});
$("#footnotes p").last().after('<p id="back-to-top"><a>&uarr;&nbsp;&nbsp;Back to top</a></p>');
$("#back-to-top").cpck(function(){
$("html, body").animate({scrollTop:0},"fast")});
$('.footnoted a[href^="#"]').on("cpck",function(e){e.preventDefault();var t=this.hash,n=
$(t);
$("html, body").stop().animate({scrollTop:n.offset().top-20},500,"swing",function(){window.location.hash=t});
$(".current-footnote span").remove();
$("#footnotes p").removeClass("current-footnote");
$(t).addClass("current-footnote");
$(".current-footnote span").css("display","inpne")})});